mapper.delete:
BEGIN

    delete from  APP_BB_XT where id=#{record.ID};

COMMIT;
END;

BEGIN

    delete from  APP_BB_SJ where id=#{record.ID};

COMMIT;
END;

BEGIN

    delete from  APP_BB_BCSBZC where id=#{record.ID};

COMMIT;
END;

mapper.select:
with a as (select oilid, bbh, count(1) ct from app_sbzc_mlralation group by oilid, bbh) select decode(status,'1','已下发','未下发') zt,to_char(rksj,'yyyymmdd hh24:mi:ss') sj,t.*, (select listagg(b.name || '(' || ct || ')', ',') within group(order by b.name) name from a, app_yq b where a.oilid = b.bzyqh and a.bbh = t.bbh) ss from APP_BB_BCSBZC t where 1=1 and deptid=#{dwdm}
and mc like '%'||#{c1}||'%'
order by bbh desc
${sort}

mapper.update:
BEGIN

    merge into APP_BB_XT a using (select #{record.ID} ID from dual) b
    on (a.ID=b.ID)
    when matched then
    update set mc=#{record.MC},
    bz=#{record.BZ}
    when not matched then
    insert (a.bbh,a.mc,a.bz,a.submitter)
    values(seq_xtbb.nextval,#{record.MC},#{record.BZ},#{submitter});

COMMIT;
END;

BEGIN

    update APP_BB_XT  set status='1' where bbh=#{record.BBH};

COMMIT;
END;

BEGIN

    merge into APP_BB_SJ a using (select #{record.ID} ID from dual) b
    on (a.ID=b.ID)
    when matched then
    update set mc=#{record.MC},typeid=#{record.TYPEID},yq=#{record.YQ},yqmc=#{record.YQMC},
    bz=#{record.BZ}
    when not matched then
    insert (a.bbh,a.mc,a.bz,a.typeid,a.yq,a.yqmc)
    values(#{record.BBH},#{record.MC},#{record.BZ},#{record.TYPEID},#{record.YQ},#{record.YQMC});

COMMIT;
END;

BEGIN

    update APP_BB_SJ  set status='1' where bbh=#{record.BBH};

COMMIT;
END;

BEGIN
merge into APP_BB_BCSBZC a using (select #{record.ID} ID from dual) b on (a.ID=b.ID) when matched then update set mc=#{record.MC}, bz=#{record.BZ}, sysj=#{record.SYSJ}, wjh=#{record.WJH}, wj=#{record.WJ} when not matched then insert (a.bbh,a.mc,a.bz,a.submitter,deptid,sysj,wjh,wj) values(SEQ_BCZBBB.nextval,#{record.MC},#{record.BZ},#{submitter},#{dwdm},#{record.SYSJ},#{record.WJH},#{record.WJ});
COMMIT; END;

BEGIN

    update APP_BB_BCSBZC  set status='1' where bbh=#{record.BBH};

COMMIT;
END;
