mapper.delete:
declare
  cursor cur_1 is
    select * from app_zb_bcralation where zhzbid = #{ID};
begin
  delete from APP_ZB_BCZH where id=#{ID} and nvl(status,'')!='1';
  for c1 in cur_1 loop
    begin
      update APP_OBJECT_BCZBTREE set zt = 0 where id = c1.bczbid;
      update APP_OBJECT_BCZBTREE a
         set zt = zt - 1
       where id =
             (select pid from APP_OBJECT_BCZBTREE where id = c1.bczbid);
    end;
  end loop;
  delete app_zb_bcralation where zhzbid = #{ID};
  commit;
end;

BEGIN

    update APP_OBJECT_DWCBBCZB set zt=0 where id=#{record.OID};
    delete from APP_ZB_MLRALATION a where zhzbid=#{fzid} and bczbid=#{record.OID};

commit;
END;

mapper.select:

select (select username from app_user where userid=w.userid) ry,to_char(rksj,'yyyymmdd hh24:mi:ss') sj,decode(status,'1','已下发','未下发') zt,w.* from APP_ZB_BCZH w where 1=1

    and deptid=#{dwdm}


    and name like '%'||#{c1}||'%'


order by rksj desc

    ${sort}
