DECLARE
v_dwrole varchar2(50):='';
BEGIN
  select id into v_dwrole from sys_office where code='999912';
  delete from SYS_USERROLE where userid=#{id} and roleid=#{role.id};
  delete from sys_userrole where userid=#{id} and roleid=v_dwrole;
commit;
end;

mapper.select:
SELECT a.* FROM app_user a
JOIN SYS_USERROLE ur ON ur.userid = a.userid AND ur.roleid = #{role.id}
WHERE a.isdel != 1 AND a.userid != '1'
ORDER BY ${page.orderBy}
ORDER BY a.username

DECLARE
v_code varchar2(100);
BEGIN
merge into sys_office a using (select #{id} id,
#{parentId} parent_id,#{name} name,#{useable} useable,#{remarks} remarks from dual) b
on (a.id=b.id)
when matched then
update set a.name=b.name,a.useable=b.useable,a.remarks=b.remarks
when not matched then
insert (a.id,a.parent_id,a.name,a.useable,a.remarks,a.type) values (sys_guid(),b.parent_id,b.name,b.useable,b.remarks,'5');
commit;
END;
