mapper.update:
DECLARE
v_id varchar2(100):='';
v_code varchar2(100):='';
BEGIN

    v_code:=SEQ_DWDM.NEXTVAL;
    merge into app_DEPT a using (select #{record.DWDM} dwdm,#{record.DWMC} dwmc,
    #{record.LXR} lxr,#{record.LXR_DH} lxr_dh,#{record.SFQY} sfqy,#{record.BZ} bz from dual) b
    on (a.DWDM= b.DWDM)
    when matched then update set A.DWMC=b.dwmc,A.LXR=lxr,A.LXR_DH=lxr_dh,A.SFQY=sfqy,A.BZ=bz
    when not matched then
    insert (a.DWDM,a.dwmc,a.lxr,a.lxr_dh,a.sfqy,a.bz)
    values(v_code,b.dwmc,b.lxr,b.lxr_dh,b.sfqy,b.bz);
    select sys_guid() into v_id from dual;
    merge into sys_office a using (select #{record.DWDM} dwdm,#{record.DWMC} dwmc from dual) b
    on (a.code=b.dwdm)
    when matched then update set a.name=b.dwmc
    when not matched then insert (id,parent_id,name,code,useable,del_flag,type)
    values(v_id,'0',b.dwmc,v_code,1,0,1);
    merge into sys_office a using (select v_code||'00' dwdm,'管理员组' dwmc from dual) b
    on (a.code=b.dwdm)
    when not matched then insert (id,parent_id,name,code,useable,del_flag,type)
    values(sys_guid(),v_id,b.dwmc,b.dwdm,1,0,2);
    merge into sys_office a using (select v_code||'01' dwdm,'全部用户组' dwmc from dual) b
    on (a.code=b.dwdm)
    when not matched then insert (id,parent_id,name,code,useable,del_flag,type)
    values(sys_guid(),v_id,b.dwmc,b.dwdm,1,0,2);

COMMIT;
END;

DECLARE v_id varchar2(100):=''; BEGIN
merge into APP_USER a using (select #{record.USERID} userid,#{record.LOGINNAME} loginname,#{record.USERNAME} username,#{record.DW} DW,#{record.GDDH} GDDH, #{record.YDDH} YDDH,#{record.EMAIL} EMAIL,#{record.BZXX} BZXX , #{record.UPWD} UPWD,#{record.PWD} PWD,#{record.SFQY} SFQY,#{record.FPZ} FPZ from dual) b on (a.userid= b.userid) when matched then update set a.username=username,a.gddh=b.gddh, a.yddh=b.yddh,a.email=b.email,a.bzxx=b.bzxx,a.upwd=b.upwd,a.pwd=b.pwd,a.sfqy=b.sfqy when not matched then insert (a.loginname,a.username,a.dw,a.gddh,a.yddh,a.email,a.bzxx,a.pwd,a.upwd,sfqy,a.fpz) values(b.loginname,b.username,b.dw,b.gddh,b.yddh,b.email,b.bzxx,'7c4a8d09ca3762af61e59520943dc26494f8941b','7c4a8d09ca3762af61e59520943dc26494f8941b',b.sfqy,b.fpz); merge into SYS_MYPAN a using (select #{record.USERID} userid,0 parent_id from dual) b on (a.id=b.userid) when not matched then insert (a.id,a.parent_id) values (b.userid,b.parent_id);
COMMIT; END;

DECLARE
v_id varchar2(100):='';
BEGIN

    update app_dept set isdel='1'  where dwdm=#{record.DWDM};
    update sys_office set del_flag='1' where code like #{record.DWCM}||'%';


COMMIT;
END;

DECLARE
v_id varchar2(100):='';
BEGIN

    update app_user set isdel='1'  where userid=#{record.USERID};

COMMIT;
END;

DECLARE
v_id varchar2(100):='';
BEGIN

    merge into SYS_USERROLE a using (select #{id} roleid,#{record.USERID} userid from dual) b
    on (a.roleid=b.roleid and a.userid=b.userid)
    when not matched then
    insert (a.roleid,a.userid) values(b.roleid,b.userid);

COMMIT;
END;
